<script>
import router from '@/router'

export default {
  name: 'listItem',
  props: ['data'],
  data(){
    return {
    }
  },
  methods:{
    jumpMenu (id) {
      router.push(`/restaurant/${id}/menu`);
    },
    jumpInfo (id) {
      router.push(`/restaurant/${id}/info`);
    },
    jumpRate (id) {
      router.push(`/restaurant/${id}/rate`);
    }
  },
  filters: {
    filDistance (distance) {
      return distance>=1000?`${(distance/1000).toFixed(2)}km`:`${distance}m`
    },
    filTime (time) {
      time = parseInt(time);
      return time<60?`${time}分钟`:`${Math.round(time/60)}小时${time%60}分钟`
    }
  }
}
</script>

